// state
import React,{Component} from "react";
import User from './User'
class App extends Component{
    state = {
        name: "sawan",
        age: "26"
    }

    render(){
        return(
            <>
                <h1>State</h1>
                <hr/>
                <h1>Name:{this.state.name} Age:{this.state.age}</h1>
            </>
        )
    }
}


