import React,{useState} from 'react';

function App() {
  let [val, setVal] = (true)
  let show=()=>{
    setVal(val?false:true)
  }
  let data1={background:"orange"}
  let data2={background:"red"}

  return (
    <>
      <h2 style={val?data1:data2}>App Componemt</h2>
      <button onClick={show}>Change Color</button>
    </>
  );
}

export default App;




