import React, { useState, useEffect } from 'react'
import axios from 'axios'
import NewsItem from './NewsItem'
function News() {
  let [ndata, setndata] = useState([])
  useEffect(() => {
    var result = axios.get(`https://newsapi.org/v2/everything?q=bitcoin&apiKey=a53f9d592bcb4b328768abfedcdcbf8b`)
    // console.log(result);
    result.then((res) => {
      // console.log(res.data.articles);
      setndata(res.data.articles)
    })
  })

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await axios.get(`https://newsapi.org/v2/everything?q=bitcoin&apiKey=a53f9d592bcb4b328768abfedcdcbf8b`);
        setndata(result.data.articles);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
  
    // Add a delay of, for example, 1 second between requests
    const delay = 1000;
    const intervalId = setInterval(fetchData, delay);
  
    // Clean up the interval when the component is unmounted
    return () => clearInterval(intervalId);
  }, []);
  
  return (
    <>
      <h2>News Data</h2>
      {ndata ? <div class="row row-cols-1 row-cols-md-2 row-cols-lg-3 row-cols-xl-4 g-4">
        {ndata.map((item) => {
          return (
            <NewsItem
              title={item.title}
              dec={item.description}
              idata={item.urlToImage}
              url={item.url}

            />
          )
        })}
      </div> : <p>No data</p>}
    </>
  )
}

export default News
