// Set state value with events
import React, { Component } from 'react';

export default class App extends Component {
  state = {
    name:"amit"
  }

  show=()=>{
    this.setState({name:"sumit"})
  }
  render() {
    return (
      <>
        <h2>{this.state.name}</h2>
        <button onClick={this.show}>Click</button>
      </>
    );
  }
}



