import React,{useState, useEffect} from 'react';

function App() {

  let [no, setno] = useState(1)
  let [val, setval] = useState(100)

  let inc=()=>{
    setno(no=no+1)
  }

  let dec=()=>{
    setval(val=val-1)
  }

  useEffect(()=>{
    console.log("use effect called...");
  }, [val])

  return (
    <>
    <p>{no}</p>
      <button onClick={inc}>Increment</button>

      <p>{val}</p>
      <button onClick={dec}>Decrement</button>
    </>
  );
}

export default App;







