// Event Handling
import React, { Component } from 'react';

export default class App extends Component {
    show=()=>{
        console.log("Bitton Clicked");
    }
  render() {
    return (
      <>
        <h1>App Class Component</h1>
        <button onClick={this.show}>Click</button>
      </>
    );
  }
}




