import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NewsItem from './NewsItem';

function News() {
  const [ndata, setNdata] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await axios.get(
          'https://newsapi.org/v2/everything?q=all&language=hi&apiKey=a53f9d592bcb4b328768abfedcdcbf8b'
        );
        setNdata(result.data.articles);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    const res = axios.get('http://' + this.ip + ':8081/BitsFlow-App/logs/getCommonLogs') // bitsflow-intg
      // axios.get('http://localhost:8082/logs/getCommonLogs') // Dynamic-Cron
      .then((response) => {
        console.log("Response form Backend: ", response);
        this.logs = response.data
        this.loader = false

        // Filter on start according to taskType
        this.filteredData = [];
        const taskType = localStorage.getItem('taskType');
        for (const log of this.logs) {
          if (log.taskType === taskType) {
            this.filteredData.push(log);
          }
        }
        this.filter();
        console.log('filteredData', this.filteredData[0]);
      })
      .catch((error) => {
        // Handle the error
        console.log("Error Occured!", error);
      })

    // Fetch data initially
    fetchData();
  }, []); // Empty dependency array ensures the effect runs only once

  return (
    <>
      <h2>News Data</h2>
      {ndata ? (
        <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 row-cols-xl-4 g-4">
          {ndata.map((item) => (
            <NewsItem
              key={item.title} // Ensure each item has a unique key
              title={item.title}
              dec={item.description}
              idata={item.urlToImage}
              url={item.url}
            />
          ))}
        </div>
      ) : (
        <p>No data</p>
      )}
    </>
  );
}

export default News;
